import React from "react";
import {Avatar, Button, Grid, Link, Paper, Typography} from '@material-ui/core'; // Grid version 2
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import TextField from '@mui/material/TextField';

const Register = ({data, onChange, handleSignIn}) => {
    // add styles
    const paperStyle = {
        padding: 20,
        height: '70vh',
        width: 350,
        margin: '20px auto'
    }

    const avatarStyle = {
        background: 'darkslateblue'
    }
    const fieldStyle = {
        margin: '10px auto'
    }
    const btnStyle = {
        //background: 'darkslateblue',
        margin: '15px 0'
    }
    // for navigating to other pages
    //let navigate = useNavigate(); 
    return (
        <Grid>
            <Paper elevation={20} style={paperStyle}>
                <Grid align='center'>
                    <Avatar style={avatarStyle}><LockOutlinedIcon></LockOutlinedIcon></Avatar>
                        <h1>Join The Club !</h1>
                </Grid>
                <TextField name="firstname" id="firstname" label="Enter Firstname" variant="outlined" fullWidth style={fieldStyle} required/>
                <TextField name="lastname" id="lastname" label="Enter Lastname" variant="outlined" fullWidth style={fieldStyle} required/>
                <TextField name="email" id="email" label="Enter Email" variant="outlined" fullWidth style={fieldStyle} required/>
                <TextField name="password" id="outlined-basic" label="Enter Password" variant="outlined" fullWidth style={fieldStyle} required type='password'/>
                <Button variant="contained" color="primary" fullWidth style={btnStyle} disabled={true} onClick={() => handleSignIn("/Home")}>Sign Up</Button>
                <Typography>
                    Already have an account ?
                    <Link href='/'>Sign In</Link>
                </Typography>
            </Paper>
        </Grid>
    )
}

export default Register;
