import React from "react";
import {Avatar, Button, Grid, Link, Paper, Typography} from '@material-ui/core'; // Grid version 2
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';

const Login = ({username, password}) => {
    // add styles
    const paperStyle = {
        padding: 20,
        height: '70vh',
        width: 280,
        margin: '20px auto'
    }
    const avatarStyle = {
        background: 'darkslateblue'
    }
    const fieldStyle = {
        margin: '10px auto'
    }
    const btnStyle = {
        background: 'darkslateblue',
        margin: '15px 0'
    }
    return (
        <Grid>
            <Paper elevation={20} style={paperStyle}>
                <Grid align='center'>
                    <Avatar style={avatarStyle}><LockOutlinedIcon></LockOutlinedIcon></Avatar>
                        <h1>Sign In</h1>
                </Grid>
                <TextField name="username" id="outlined-basic" label="Username" variant="outlined" fullWidth style={fieldStyle} required/>
                <TextField name="password" id="outlined-basic" label="Password" variant="outlined" fullWidth style={fieldStyle} required type='password'/>
                <FormControlLabel control={<Checkbox defaultChecked />} label="Remember me" />
                <Button type="submit" variant="contained" color="primary" fullWidth style={btnStyle}>Log in</Button>
                <Typography>
                    <Link href='#'>Forgot password ?</Link>
                </Typography>
                <Typography>
                    <Link href='#'>Sign Up</Link>
                </Typography>
            </Paper>
        </Grid>
    )
}

export default Login;
