import React, { useState } from 'react';
import Login from './components/Login';
//import Register from './components/Register';

function App() {
  // create a dataset
  const [data, setdata] = useState({
    username: "",
    password: "",
    disabled: true
  })

  const handleInputChange = event => {
    event.preventDefault();
    // change the state as the user types in the data and simultaneously insert the values inside the state
    const newData = {...data};
    newData[event.target.name] = event.target.value;
    // check if the username and password has been filled or not
    if(newData["usename"].length() > 1 && newData["password"] >= 8){
      newData["disabled"] = false;
    }
    console.log(newData);
    setdata(newData);
  }

  return (
    <div className="App">
      <Login data={data} onChange={handleInputChange}/>
    </div>
  );
}

export default App;
