var eventHandler = () => {
    // handler function
    var handlerInp = {
        // CHECK OF VALID EMAIL
        isValidEmail: (e) => {
            if(e.target.name === "email"){
                let mail = e.target.value;
                let validEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
                if(!validEmail.test(mail)){
                  //console.log("Password should be atleast 8 characters.")
                  return false;
                }
                else{
                  //data[e.target.name] = mail;
                  // set valid password as true
                  return true;
                  //console.log(newSignUpData["passwordValid"]);
                }
              }
        },
        // CHECK FOR VALID USERNAME
        isValidUsername: (e) => {
            if(e.target.name === "username"){
                let user = e.target.value;
                if(user.length < 5){
                  return false;
                }
                else{
                  return true;
                }
              }
        },

        // CHECK FOR PASSWORD VALIDATION
        isValidPassword: (e) => {
            if(e.target.name === "password"){
                let validPass = /(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.{8,})/;
                let pass = e.target.value;
                if(!pass.match(validPass)){
                    //console.log("Password should be atleast 8 characters.")
                    return false;
                }
                else{
                  return true;
                }

              }
        },
        signIn: (event, signInData) => {
            event.preventDefault();
            // change the state as the user types in the data and simultaneously insert the values inside the state
            const newData = {...signInData};
            // condition ? expression on true : expression on false
            // check if Remember me checkboc is checked and set the state of the checkbox to true
            event.target.name === "rememberCheckbox" ? newData[event.target.name] = event.target.checked : newData[event.target.name] = event.target.value;
            console.log(newData);
            return {event, newData};
            //setSignInData(newData);
            //console.log(newData);
          }

    }
    //console.log(handlerInp);
    return handlerInp;
}

export default eventHandler;
