import React, { useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Home from './components/Home';

function App() {
  // used to navigate to other pages
  let navigate = useNavigate();

  // create a dataset for login
  const [signInData, setSignInData] = useState({
    username: "",
    password: "",
    rememberCheckbox: false
  })

  // create a dataset for register
  const [signUpData, setSignUpData] = useState({
    firstname: "",
    lastname: "",
    username: "",
    email: "",
    password: ""
  })

  const handleSignInInputChange = event => {
    event.preventDefault();
    // change the state as the user types in the data and simultaneously insert the values inside the state
    const newData = {...signInData};
    // condition ? expression on true : expression on false
    // check if Remember me checkboc is checked and set the state of the checkbox to true
    event.target.name === "rememberCheckbox" ? newData[event.target.name] = event.target.checked : newData[event.target.name] = event.target.value;
    console.log(newData);
    setSignInData(newData);
    //console.log(newData);
  }

  const handleSignUpInputChange = event => {
    event.preventDefault();
    // change the state as the user types in the data and simultaneously insert the values inside the state
    const newSignUpData = {...signUpData};
    // condition ? expression on true : expression on false
    // check if Remember me checkboc is checked and set the state of the checkbox to true
    event.target.name === "rememberCheckbox" ? newSignUpData[event.target.name] = event.target.checked : newSignUpData[event.target.name] = event.target.value;
    console.log(newSignUpData);
    setSignUpData(newSignUpData);
    //console.log(newSignUpData);
  }
  /*
  const handleSignUp = event => {
    event.preventDefault();
    // fetch data from local host
    fetch("http://localhost:3000/Register",{
      method: "POST",
      body: JSON.stringify(signUpData),
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    })
    .then(response => response.json)
    .then(data => setSignUpData(data))
    .then(msg => console.log(msg))
    .catch(err => console.log(err))

    // clear the inputs
    setSignUpData({
      firstName: "",
      lastName: "",
      username: "",
      email: "",
      password: ""
    });

  }
  */

  function test(event) {
    event.preventDefault();
    console.log("onChange is a function")
    const newdata = [...signInData]
    newdata[event.target.name] = event.target.value
    console.log(newdata)
    setSignInData(newdata);
  }

  return (
    <div className="App">
      <Routes>
        <Route path="*" element={<Login data={signInData} onChange={handleSignInInputChange} handleSignIn={navigate} />}/>
        <Route path="/register" element={<Register data={signUpData} onChange={handleSignUpInputChange} handleSignUp={navigate} />} />
        <Route path="/home" element={<Home data={signInData} onChange={test()}/>} />
      </Routes>
    </div>
  );
}

export default App;
