const validator = require('./validator.js');

var eventHandler = () => {
    var validator = validator();
    // handler function
    var handlerInp = {
        handleSignUpInputChange: (event, signUpData) => {
            event.preventDefault();
            const newSignUpData = {...signUpData};
            // VALID EMAIL
            if(validator.isValidEmail(event)===true){
                newSignUpData[event.target.name]=event.target.value;
                newSignUpData["emailValid"]=true;

            } else{
                newSignUpData["emailValid"] = false
            }
            return {event, newSignUpData};

        }
    }
    //console.log(handlerInp);
    return handlerInp;
}

module.exports = eventHandler;
