var eventHandler = () => {
    // handler function
    var handlerInp = {
        // CHECK OF VALID EMAIL
        isValidEmail: (e) => {
            if(e.target.name === "email"){
                let mail = e.target.value;
                let validEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
                if(!validEmail.test(mail)){
                  //console.log("Password should be atleast 8 characters.")
                  return false;
                }
                else{
                  //data[e.target.name] = mail;
                  // set valid password as true
                  return true;
                  //console.log(newSignUpData["passwordValid"]);
                }
              }
        },
        // CHECK FOR VALID USERNAME
        isValidUsername: (e) => {
            if(e.target.name === "username"){
                let user = e.target.value;
                if(user.length < 5){
                  return false;
                }
                else{
                  return true;
                }
              }
        },

        // CHECK FOR PASSWORD VALIDATION
        isValidPassword: (e) => {
            if(e.target.name === "password"){
                let validPass = /(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.{8,})/;
                let pass = e.target.value;
                if(!pass.match(validPass)){
                    //console.log("Password should be atleast 8 characters.")
                    return false;
                }
                else{
                  return true;
                }

              }
        },

        // SIGN UP HANDLER
        handleSignUpInputChange: (event, signUpData, setSignUpData) => {
            event.preventDefault();
            // change the state as the user types in the data and simultaneously insert the values inside the state
            const newSignUpData = {...signUpData};

            // VALID EMAIL
            if(handlerInp.isValidEmail(event)===true){
                newSignUpData[event.target.name]=event.target.value;
                newSignUpData["emailValid"]=true;

            } else{
                newSignUpData["emailValid"] = false
            }

            // USERNAME VALIDATION
            if(handlerInp.isValidUsername(event)===true){
                newSignUpData[event.target.name] = event.target.value;
                // set username valid to true
                newSignUpData["usernameValid"] = true;
            }
            else{
                newSignUpData["usernameValid"] = false;
            }

            // PASSWORD VALIDATION
            if(handlerInp.isValidPassword(event)===true){
                newSignUpData[event.target.name] = event.target.value;
                // set username valid to true
                newSignUpData["passwordValid"] = true;
            }
            else{
                newSignUpData["passwordValid"] = false;
            }

            // for all other things
            newSignUpData[event.target.name] = event.target.value;

            console.log(newSignUpData);
            setSignUpData(newSignUpData);
            return {signUpData, setSignUpData};
            //console.log(newSignUpData);
        }

    }
    return handlerInp;


}

module.exports = eventHandler;
