import React, { useState } from 'react';
import { Routes, Route, BrowserRouter } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Home from './components/Home';

function App() {
  // used to navigate to other pages
  //let navigate = useNavigate();

  // create a dataset for login
  const [signInData, setSignInData] = useState({
    username: "",
    password: "",
    rememberCheckbox: false
  })

  // create a dataset for register
  const [signUpData, setSignUpData] = useState({
    firstname: "",
    lastname: "",
    username: "",
    email: "",
    password: ""
  })

  const handleSignInInputChange = event => {
    event.preventDefault();
    // change the state as the user types in the data and simultaneously insert the values inside the state
    const newData = {...signInData};
    // condition ? expression on true : expression on false
    // check if Remember me checkboc is checked and set the state of the checkbox to true
    event.target.name === "rememberCheckbox" ? newData[event.target.name] = event.target.checked : newData[event.target.name] = event.target.value;
    console.log(newData);
    setSignInData(newData);
    //console.log(newData);
  }

  const handleSignUpInputChange = event => {
    event.preventDefault();
    // change the state as the user types in the data and simultaneously insert the values inside the state
    const newSignUpData = {...signUpData};
    // condition ? expression on true : expression on false
    // check if Remember me checkboc is checked and set the state of the checkbox to true
    //event.target.name === "password" ? newSignUpData[event.target.name] = event.target.checked : newSignUpData[event.target.name] = event.target.value;

    let validPassLength = 8;
    let pass = event.target.value;
    console.log("Length of password: " + pass.length);
    event.target.name === "password" && pass.length >= validPassLength ? newSignUpData[event.target.name] = event.target.value : console.log("Password should be atleast 8 characters");
    //onsole.log("target name: " + event.target.name);
    //newSignUpData[event.target.name] = pass

    console.log(newSignUpData);
    setSignUpData(newSignUpData);
    //console.log(newSignUpData);
  }

  const [homeData, setHomeData] = useState({
    email: ""
  })

  const test = event => {
    event.preventDefault();
    const newHomeData = {...homeData}
    newHomeData[event.target.name] = event.target.value
    console.log(newHomeData)
    setHomeData(newHomeData);
  }

  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route index element={<Login data={signInData} onChange={handleSignInInputChange}/>} />
          <Route path='/login' element={<Login data={signInData} onChange={handleSignInInputChange}/>}/>
          <Route path="/register" element={<Register data={signUpData} onChange={handleSignUpInputChange}/>} />
          <Route path='/home' element={<Home test={test}/>}/>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
