import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Route, Link } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';

function App() {
  // used to navigate to other pages
  let navigate = useNavigate();

  // create a dataset for login
  const [signInData, setSignInData] = useState({
    username: "",
    password: "",
    rememberCheckbox: false
  })

  // create a dataset for login
  const [signUpData, setSignUpData] = useState({
    firstname: "",
    lastname: "",
    username: "",
    email: "",
    password: ""
  })

  const handleSignInInputChange = event => {
    event.preventDefault();
    // change the state as the user types in the data and simultaneously insert the values inside the state
    const newData = {...signInData};
    // condition ? expression on true : expression on false
    // check if Remember me checkboc is checked and set the state of the checkbox to true
    event.target.name === "rememberCheckbox" ? newData[event.target.name] = event.target.checked : newData[event.target.name] = event.target.value;
    //console.log(newData);
    setSignInData(newData);
    console.log(newData);
  }

  const handleSignUpInputChange = event => {
    event.preventDefault();
    // change the state as the user types in the data and simultaneously insert the values inside the state
    const newData = {...signUpData};
    // condition ? expression on true : expression on false
    // check if Remember me checkboc is checked and set the state of the checkbox to true
    newData[event.target.name] = event.target.value;
    //console.log(newData);
    setSignUpData(newData);
    console.log(newData);
  }

  const handleSignUp = event => {
    event.preventDefault();
    // fetch data from local host
    fetch("http://localhost:3000",{
      method: "POST",
      body: JSON.stringify(signUpData),
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    })
    .then(response => response.json)
    .then(data => setSignUpData(data))
    .then(msg => console.log(msg))
    .catch(err => console.log(err))

    // clear the inputs
    setSignUpData({
      firstName: "",
      lastName: "",
      username: "",
      email: "",
      password: ""
    });

  }

  return (
    <div className="App">
      <Route exact path="/" component={Login} data={signInData} onChange={handleSignInInputChange} handleSignIn={navigate}/>
      <Route exact path="/register" component={Register} data={signUpData} onChange={handleSignUpInputChange} handleSignUp={handleSignUp}/>
    </div>
  );
}

export default App;
