import React from "react";
import {Avatar, Button, Grid, Paper} from '@material-ui/core'; // Grid version 2
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';

const Login = () => {
    // add styles
    const paperStyle = {
        padding: 20,
        height: '70vh',
        width: 280,
        margin: '20px auto'
    }
    const avatarStyle = {
        background: 'primary'//'#4138FF'
    }
    const fieldStyle = {
        margin: '10px auto'
    }

    return (
        <Grid>
            <Paper elevation={20} style={paperStyle}>
                <Grid align='center'>
                    <Avatar style={avatarStyle}><LockOutlinedIcon></LockOutlinedIcon></Avatar>
                        <h1>Sign In</h1>
                </Grid>
                <TextField id="outlined-basic" label="Username" variant="outlined" fullWidth style={fieldStyle} required/>
                <TextField id="outlined-basic" label="Password" variant="outlined" fullWidth style={fieldStyle} required type='password'/>
                <FormControlLabel control={<Checkbox defaultChecked />} label="Remember me" />
                <Button type="submit" variant="contained" color="primary" fullWidth>Log in</Button>
            </Paper>
        </Grid>
    )
}

export default Login;
